import React, { PureComponent } from 'react';
// import mnData from '../server.js'

import {
    BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

const data = [
    {
        name: 'Page A', IL: 4000, MN: 2400
    },
    {
        name: 'Page B', IL: 3000, MN: 1398
    },
    {
        name: 'Page C', IL: 2000, MN: 11800
    },
    {
        name: 'Page D', IL: 2780, MN: 3908
    },
    {
        name: 'Page E', IL: 1890, MN: 4800
    },
    {
        name: 'Page F', IL: 2390, MN: 3800
    },
    {
        name: 'Page G', IL: 3490, MN: 4300
    },
];

export default class Example extends PureComponent {
    static jsfiddleUrl = 'https://jsfiddle.net/alidingling/30763kr7/';
    state = {
        data: null
    }

    componentDidMount() {
        this.callBackendApi()
        .then( res => this.setState({data: res.express}))
        .catch(err => console.log(err));
    }

    callBackendApi = async() => {
        const response = await fetch("/data?url=https://www.health.state.mn.us/diseases/coronavirus/situation.html");
        const body = await response.json();

        if(response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    }

    render() {
        return (
            <BarChart
                width={500}
                height={300}
                data={this.state.data}
                margin={{
                    top: 5, right: 30, left: 20, bottom: 5,
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="MN_new" fill="#82ca9d" />
                <Bar dataKey="MN_total" fill="#008080" />
            </BarChart>
        );
    }
}
