import React, { PureComponent, Component } from 'react';
import {
    BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';


class Example extends PureComponent {
    // static jsfiddleUrl = 'https://jsfiddle.net/alidingling/30763kr7/';
    state = {
        mnData: null,
        ilData: null,
        mergedData: null
    };

    componentDidMount() {
        // Call our fetch function below once the component mounts
        this.fetchMnData()
            .then(res => this.setState({ mnData: res.data }))
            .then(res => console.log("response: " + res))
            .catch(err => console.log(err));
        this.fetchIlData()
            .then(res => this.setState({ ilData: res.data }, this.fetchMergedData))
            .then(res => console.log("response: " + res))
            .catch(err => console.log(err));



    }

    fetchMnData = async () => {
        const response = await fetch('/api/data?url=https://www.health.state.mn.us/diseases/coronavirus/situation.html');
        console.log("got response");
        const body = await response.json();

        if(response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    fetchIlData = async () => {
        const response = await fetch('/api/ilData?url=https://en.wikipedia.org/wiki/2020_coronavirus_pandemic_in_Illinois');
        console.log("got response");
        const body = await response.json();

        if(response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    fetchMergedData = () => {
        var mData = this.state.mnData.concat(this.state.ilData);
        const newData = mData.filter(function(v) {
            return this[v.name]?
              !Object.assign(this[v.name], v):
              (this[v.name] = v);
          }, {});
        console.log(newData);
        return newData;
    }

    render() {
        console.log("data: " + this.state.mnData);
        return (
            <div>
                <BarChart
                    width={750}
                    height={300}
                    data={this.state.mnData}
                    margin={{
                        top: 5, right: 30, left: 20, bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="MN_new" fill="#82ca9d" />
                    <Bar dataKey="MN_total" fill="#008080" />
                </BarChart>
                <BarChart
                    width={750}
                    height={300}
                    data={this.state.ilData}
                    margin={{
                        top: 5, right: 30, left: 20, bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="IL_new" fill="#82ca9d" />
                    <Bar dataKey="IL_total" fill="#008080" />
                </BarChart>
            </div>
        );
    }
}

export default Example;
